import React from 'react';
import {AppBar, Toolbar, Typography, makeStyles, Hidden, Grid} from '@material-ui/core';
import TitleIcon from '@material-ui/icons/Title';
import { Link } from 'react-router-dom';
import { Link as Scroll } from 'react-scroll';

const useStyles = makeStyles((theme) => ({
    typographyStyles: {
        flex: 1,
        color: 'rgb(228, 130, 74)',
        fontSize: '40px',
        marginTop: '30px',
        marginBottom: '20px',
        marginLeft: '20px',
        marginRight: '10px'
    },

    root: {
        boxShadow: "none",
        backgroundColor: "#222222",
        height: 'auto'
    },

    titleIcon: {
        color: 'rgb(228, 130, 74)',
        marginTop: '10px',
        marginRight: '0px',
        fontSize: '50px',
        border: 'solid 2px rgb(228, 130, 74)'

    },

    link:{
        color: 'rgb(228, 130, 74)',
        marginTop: '20px',
        width: '100px',
        height: '60px',
        textDecoration: 'none',
        '&:hover': {
            textDecoration: 'underline'
        },
        cursor: 'pointer',
        textAlign: 'left'
    },

    linkTypography:{
        fontSize: '30px',
        paddingTop: '5px'
    }
    
}));

const NavBar = () => {
    const classes = useStyles();
    return(
        <AppBar position="static" className={classes.root} id="navBar">
            <Toolbar>
                <Grid container direction="row">
                    <Grid item xs={3} sm={9}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>Home</Typography></Link>
                    </Grid>
                    <Grid item xs={3} sm={false}>
                        <Hidden smUp>
                            <Scroll 
                                activeClass="active"
                                to="projects"
                                spy={true}
                                smooth={true}
                                offset={-70}
                                duration= {500} 
                                className={classes.link}
                            >
                                    <Typography className={classes.linkTypography}>
                                        Projects
                                    </Typography>
                            </Scroll>
                        </Hidden>
                    </Grid>
                    <Grid item xs={3} sm={false}>
                        <Hidden smUp><Link to='/AboutMe' className={classes.link}><Typography className={classes.linkTypography}>AboutMe</Typography></Link></Hidden>
                    </Grid>
                    <Grid item xs={3} sm={1}>
                        <TitleIcon className={classes.titleIcon}/>
                    </Grid>
                </Grid>
            </Toolbar>
        </AppBar>
    )
}

export default NavBar;
