import React from 'react';
import {AppBar, Toolbar, Typography, makeStyles, Hidden, Grid} from '@material-ui/core';
import TitleIcon from '@material-ui/icons/Title';
import { Link } from 'react-router-dom';
import { Link as Scroll } from 'react-scroll';

const useStyles = makeStyles((theme) => ({
    root: {
        boxShadow: "none",
        backgroundColor: "#222222",
        height: 'auto',
    },
    
    gridContainer:{
        flex: 1,
        marginTop: '20px',
        marginBottom: '10px',
    },

    titleIcon: {
        color: 'rgb(228, 130, 74)',
        marginTop: '10px',
        marginRight: '10px',
        marginBottom: '10px',
        fontSize: '40px',
        border: 'solid 2px rgb(228, 130, 74)'

    },

    link:{
        color: 'rgb(228, 130, 74)',
        width: '20px',
        height: 'auto',
        margin: '0 auto'
    },

    scrollLink:{
        color: 'rgb(228, 130, 74)',
        width: '20px',
        height: 'auto',
        margin: '0 auto'
    },

    linkTypography:{
        fontSize: '22px',
        paddingTop: '0px',
        fontFamily: 'roboto',
        '&:hover':{
            textDecoration: 'underline'
        }
    },

    linkContainer:{
        textAlgin: 'left',
        margin: '0px',
        width: 'auto',
        marginLeft: '20px'
    }
    
}));

const NavBar = () => {
    const classes = useStyles();
    return(
        <AppBar position="static" className={classes.root} id="navBar">
            <Toolbar>
                <Grid container direction='row' className={classes.gridContainer}>
                    <Grid item className={classes.linkContainer} style={{marginLeft: '5px'}}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>Home</Typography></Link>
                    </Grid>
                    <Grid item className={classes.linkContainer}>
                        <Scroll 
                            className={classes.scrollLink}
                        >
                            <Typography className={classes.linkTypography}>
                                Projects
                            </Typography>
                        </Scroll>
                    </Grid>
                    <Grid item className={classes.linkContainer}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>About</Typography></Link>
                    </Grid>
                    <Grid item className={classes.linkContainer}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>Contact</Typography></Link>
                    </Grid>
                    <Grid item className={classes.linkContainer}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>Resume</Typography></Link>
                    </Grid>
                </Grid>
                <Hidden xsDown>
                    <TitleIcon className={classes.titleIcon}/>
                </Hidden>
            </Toolbar>
        </AppBar>
    )
}

export default NavBar;
