import React from 'react';
import {AppBar, Toolbar, Typography, makeStyles, Hidden, Grid} from '@material-ui/core';
import TitleIcon from '@material-ui/icons/Title';
import { Link } from 'react-router-dom';
import { Link as Scroll } from 'react-scroll';

const useStyles = makeStyles((theme) => ({
    typographyStyles: {
        color: 'rgb(228, 130, 74)',
        fontSize: '5px',
        marginTop: '30px',
        marginBottom: '10px',
        width: 'auto'
    },

    root: {
        boxShadow: "none",
        backgroundColor: "#222222",
        height: 'auto'
    },

    titleIcon: {
        color: 'rgb(228, 130, 74)',
        width: 'auto',
        marginTop: '15px',
        marginRight: '10px',
        marginBottom: '10px',

        fontSize: '40px',
        border: 'solid 2px rgb(228, 130, 74)'

    },

    link:{
        color: 'rgb(228, 130, 74)',
        marginTop: '20px',
        width: 'auto',
        height: '60px',
        textDecoration: 'none',
        '&:hover': {
            textDecoration: 'underline'
        },
        cursor: 'pointer',
        textAlign: 'left',
    },

    gridContainer:{
        width: '100%'
    },

    titleIconGrid:{
        textAlign: 'right',
        display: 'inline-break',
    },

    linkTypography:{
        fontSize: '20px',
        paddingTop: '5px',
        marginTop: '15px'
    }
    
}));

const NavBar = () => {
    const classes = useStyles();
    return(
        <AppBar position="static" className={classes.root} id="navBar">
            <Toolbar>
                <Grid container direction="row" className={classes.gridContainer}>
                    <Grid item xs={3} sm={3}>
                        <Link to='/' className={classes.link}><Typography className={classes.linkTypography}>Home</Typography></Link>
                    </Grid>
                    <Grid item xs={3} sm={false}>
                        <Hidden smUp>
                            <Scroll 
                                activeClass="active"
                                to="projects"
                                spy={true}
                                smooth={true}
                                offset={-70}
                                duration= {500} 
                                className={classes.link}
                            >
                                    <Typography className={classes.linkTypography}>
                                        Projects
                                    </Typography>
                            </Scroll>
                        </Hidden>
                    </Grid>
                    <Grid item xs={3} sm={false}>
                        <Hidden smUp>
                            <Link to='/AboutMe' className={classes.link}>
                                <Typography className={classes.linkTypography}>
                                    About
                                </Typography>
                            </Link>
                        </Hidden>
                    </Grid>
                    
                    <Grid item xs={3} sm={3} className={classes.titleIconGrid}>
                        <TitleIcon className={classes.titleIcon}/>
                    </Grid>
                </Grid>
            </Toolbar>
        </AppBar>
    )
}

export default NavBar;
